mapper.select:
select count(*)
from TB_COMMUNITY
where COMMUNITYNAME = #{communityname}

select communityId
from TB_COMMUNITY
where COMMUNITYNAME =#{communityname}

select *
from (select communityid, communityname, communitydate, user_id, communityimgori, communityimgrename, communitydesc, ROWNUM
      from TB_COMMUNITY)
where ROWNUM >= #{ startRow } and ROWNUM <= #{ endRow }
